mapper.update:
UPDATE complaint
SET
    status = #{status},
    received_datetime = #{receivedDatetime}
WHERE complaint_id = #{complaintId}

UPDATE complaint
SET
    delete_flag = #{deleteFlag}
WHERE complaint_id = #{complaintId}

mapper.select:
SELECT
    product_total_price
FROM order_detail
WHERE order_id = #{orderId}
AND product_name = #{productName}
