    total_amount, total_quantity, order_datetime
)
VALUES (
    #{orderId}, #{customerId}, #{orderStatus}, #{totalDiscountAmount},
    #{totalOrderAmount}, #{totalQuantity}, #{orderDatetime}
)

mapper.update:
UPDATE payment
SET status = #{status}
WHERE payment_id = #{paymentId}

mapper.insert:
