mapper.update:
update shipping
tracking_number = #{trackingNumber}, shipping_datetime = #{shippingDatetime}, expected_datetime = #{expectedDatetime}, end_datetime = #{endDatetime}, note = #{note}, delay_reason = #{delayReason}, description = #{description}, delivery_name = #{deliveryName}, delivery_number = #{deliveryNumber}, delivery_datetime = #{deliveryDatetime}, updated_at = NOW()
where shipping_id = #{shippingId}
